package demo.rpn;

import java.util.Scanner;

import demo.calculator.RpnCalculator;

public class App {
    private static RpnCalculator rpnCalculator = new RpnCalculator();
    private static Scanner scanner = new Scanner(System.in);

    public static void main(String[] args) {
        while (true) {
            System.out.println("Please input commands. Type 'quit' to quit.");
            String input = scanner.nextLine();
            if (input.equals("quit")) {
                return;
            }
            System.out.println(rpnCalculator.process(input));
        }
    }
}
